package com.mr3y.ludi.core.network.datasources.internal

import com.google.testing.junit.testparameterinjector.TestParameter
import com.google.testing.junit.testparameterinjector.TestParameterInjector
import com.mr3y.ludi.core.model.Result
import com.mr3y.ludi.core.network.model.GiantBombArticle
import com.prof.rssparser.*
import io.mockk.coEvery
import io.mockk.mockk
import kotlinx.coroutines.ExperimentalCoroutinesApi
import kotlinx.coroutines.test.runTest
import org.junit.BeforeClass
import org.junit.Test
import org.junit.runner.RunWith
import strikt.api.expectThat
import strikt.assertions.isA
import strikt.assertions.isEqualTo

@OptIn(ExperimentalCoroutinesApi::class)
@RunWith(TestParameterInjector::class)
class GiantBombDataSourceTest {

    @Test
    fun whenFetchingRSSFeedSuccessfully_dataIsTransformedProperly(@TestParameter rssFeedUrlWithSampleData: RSSFeedURLWithSampleData) = runTest {
        // given a mocked request with sample data
        coEvery { rssParser.getChannel(rssFeedUrlWithSampleData.url) } returns rssFeedUrlWithSampleData.channel

        // when trying to fetch rss feed
        val result = when(rssFeedUrlWithSampleData.url) {
            GiantBombDataSource.RSSNewsFeedURL -> sut.fetchNewsFeed()
            GiantBombDataSource.RSSNewReleasesFeedURL -> sut.fetchNewReleasesFeed()
            GiantBombDataSource.RSSReviewsFeedURL -> sut.fetchReviewsFeed()
            else -> throw IllegalArgumentException()
        }

        // then assert we got the sample data parsed & transformed to our typed model
        expectThat(result).isA<Result.Success<List<GiantBombArticle>>>()
        result as Result.Success
        expectThat(result.data).isEqualTo(rssFeedUrlWithSampleData.expectedArticles)
    }

    @Test
    fun whenFailingToFetchRSSFeed_exceptionIsWrapped(@TestParameter rssFeedUrlWithSampleData: RSSFeedURLWithSampleData) = runTest {
        // Assuming there is a failure while trying to fetch rss feed
        coEvery { rssParser.getChannel(rssFeedUrlWithSampleData.url) } throws Exception()

        // when trying to fetch rss feed
        val result = when(rssFeedUrlWithSampleData.url) {
            GiantBombDataSource.RSSNewsFeedURL -> sut.fetchNewsFeed()
            GiantBombDataSource.RSSNewReleasesFeedURL -> sut.fetchNewReleasesFeed()
            GiantBombDataSource.RSSReviewsFeedURL -> sut.fetchReviewsFeed()
            else -> throw IllegalArgumentException()
        }

        // then assert the exception is caught and wrapped into a typed Result.Error
        expectThat(result).isA<Result.Error>()
    }

    enum class RSSFeedURLWithSampleData(val url: String, val channel: Channel, val expectedArticles: List<GiantBombArticle>) {
        NewsFeed(
            GiantBombDataSource.RSSNewsFeedURL,
            Channel(
                title = "Video Game News at Giant Bomb",
                link = "https://www.giantbomb.com/feeds/news/",
                description = "Giant Bomb's Video Game News",
                lastBuildDate = "Wed, 01 Mar 2023 12:07:38 -0800",
                articles = listOf(
                    Article(
                        title = "The Community Spotlight 2023.02.25",
                        link = "https://www.giantbomb.com/articles/the-community-spotlight-2023-02-25/1100-6288/",
                        description = "<![CDATA[ <p>Welcome to the latest edition of the Giant Bomb Community Spotlight! I, <a href=\"https://www.giantbomb.com/profile/zombiepie/\">@zombiepie</a>, am once again honored to be your host as we look back at the best community-created works from the previous week.</p><p>After a delay due to recent events, <a href=\"https://www.giantbomb.com/articles/community-goty-2022-winners/1100-6280/\"><strong>the Giant Bomb Community GOTY 2022 Winners have been announced!</strong></a> If you want to see how the community vote stacked up with the staff GOTY awards, or just like this feature, drop a supportive comment in the article!</p><p>Dylan Host, the Community Manager of Safety for Fandom’s Community Safety team <a href=\"https://www.giantbomb.com/forums/general-discussion-30/introducing-myself-1909989/\"><strong>wants to introduce himself</strong></a>! What's his job and when should you contact him? Some people are confused as to how their overlaps with Rorie's and, as they say in the write-up, they are happy to respond to any comments you drop their way.</p><p>This next note is a throwback to those of you that have been on Giant Bomb for a while. Drew Scanlon and Danny O'Dwyer have <a href=\"https://twitter.com/dannyodwyer/status/1628423721601097730\"><strong>a new episode of their F1 podcast available</strong></a>. Give it a listen if you want a fun rundown of all the upcoming events for the season and other news connected to F1! Likewise, <a href=\"https://www.patreon.com/gamemess\"><strong>Grubb's off-site Game Mess Patreon</strong></a> is nearing the 250 patrons mark and thanks anyone who joins.</p><p>Finally, if silly but fun stuff like this does not sell you on why you should join <a href=\"https://discord.com/invite/giantbomb\"><strong>the Official Giant Bomb Discord</strong></a>, then I don't know what will.</p><figure data-align=\"center\" data-size=\"large\" data-img-src=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449843-2928880459-FpnVryHaEAA9IYO.jpg\" data-ref-id=\"1300-3449843\" data-ratio=\"0.44688922610015\" data-width=\"1318\" data-embed-type=\"image\" style=\"width: 1318px\"><a href=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449843-2928880459-FpnVryHaEAA9IYO.jpg\"><img alt=\"No Caption Provided\" src=\"https://www.giantbomb.com/a/uploads/scale_super/0/8273/3449843-2928880459-FpnVryHaEAA9IYO.jpg\" srcset=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449843-2928880459-FpnVryHaEAA9IYO.jpg 1318w, https://www.giantbomb.com/a/uploads/scale_super/0/8273/3449843-2928880459-FpnVryHaEAA9IYO.jpg 960w, https://www.giantbomb.com/a/uploads/scale_medium/0/8273/3449843-2928880459-FpnVryHaEAA9IYO.jpg 480w\" sizes=\"(max-width: 960px) 100vw, 960px\" data-width=\"960\"></a></figure><h2>Tweets</h2><div data-embed-type=\"tweet\" data-src=\"https://twitter.com/christilton/status/1629217909137633280\" data-has-image=\"true\"><blockquote align=\"center\" class=\"twitter-tweet\" data-conversation=\"none\" data-mce-disable-toolbar=\"true\"><p dir=\"ltr\">Hey, <a href=\"https://twitter.com/DanRyckert?ref_src=twsrc^tfw\">@DanRyckert</a> &amp; <a href=\"https://twitter.com/JeffGrubb?ref_src=twsrc^tfw\">@JeffGrubb</a>, I’d like to nominate a game I scored for Blight Club: Jumper Griffin’s Story. <a href=\"https://t.co/VkKtnzlFwj\">pic.twitter.com/VkKtnzlFwj</a></p> — Chris Tilton (@christilton) <a href=\"https://twitter.com/christilton/status/1629217909137633280?ref_src=twsrc^tfw\">February 24, 2023</a></blockquote> <text></text> </div><div data-embed-type=\"tweet\" data-src=\"https://twitter.com/GB_Nonsense/status/1629174931480104960\" data-has-image=\"false\"><blockquote align=\"center\" class=\"twitter-tweet\" data-conversation=\"none\" data-mce-disable-toolbar=\"true\"><p dir=\"ltr\">Jeff B: I haven't heard it since the\"event.\"<br />Grubb: That was going to be my question: is it following you around like that thing where a ghost follows you around until you have sex with someone else?<br />Jeff B: Oh my God, I need to hear more about why that's a thing.<a href=\"https://twitter.com/hashtag/Bombcast773?src=hash&amp;ref_src=twsrc^tfw\">#Bombcast773</a></p> — Giant Bomb Nonsense (@GB_Nonsense) <a href=\"https://twitter.com/GB_Nonsense/status/1629174931480104960?ref_src=twsrc^tfw\">February 24, 2023</a></blockquote> <text></text> </div><div data-embed-type=\"tweet\" data-src=\"https://twitter.com/therealtaaltos/status/1628443581945118721\" data-has-image=\"true\"><blockquote align=\"center\" class=\"twitter-tweet\" data-conversation=\"none\" data-mce-disable-toolbar=\"true\"><p dir=\"ltr\">The <a href=\"https://twitter.com/giantbomb?ref_src=twsrc^tfw\">@giantbomb</a> chat during <a href=\"https://twitter.com/hashtag/GameMessMornings?src=hash&amp;ref_src=twsrc^tfw\">#GameMessMornings</a> with <a href=\"https://twitter.com/JeffGrubb?ref_src=twsrc^tfw\">@JeffGrubb</a> is always a good ass time! If you want the link to the monstrosity mentioned, just ask. <a href=\"https://t.co/9xVouGOk2g\">pic.twitter.com/9xVouGOk2g</a></p> — Joey Gills (@therealtaaltos) <a href=\"https://twitter.com/therealtaaltos/status/1628443581945118721?ref_src=twsrc^tfw\">February 22, 2023</a></blockquote> <text></text> </div><div data-embed-type=\"tweet\" data-src=\"https://twitter.com/MooseyMcMan/status/1628013756704866304\" data-has-image=\"false\"><blockquote align=\"center\" class=\"twitter-tweet\" data-conversation=\"none\" data-mce-disable-toolbar=\"true\"><p dir=\"ltr\">Ten years ago the world learned about Brad Muir's ska band. <a href=\"https://t.co/oppPvdNJkK\">https://t.co/oppPvdNJkK</a></p> — Moosey. (@MooseyMcMan) <a href=\"https://twitter.com/MooseyMcMan/status/1628013756704866304?ref_src=twsrc^tfw\">February 21, 2023</a></blockquote> <text></text> </div><div data-embed-type=\"tweet\" data-src=\"https://twitter.com/danauer/status/1627808996836118528\" data-has-image=\"true\"><blockquote align=\"center\" class=\"twitter-tweet\" data-conversation=\"none\" data-mce-disable-toolbar=\"true\"><p dir=\"ltr\">There’s been a lot of variations on the logo over the years but this was objectively the best one <a href=\"https://t.co/ttPnaOoECv\">pic.twitter.com/ttPnaOoECv</a></p> — Dan Auer (@danauer) <a href=\"https://twitter.com/danauer/status/1627808996836118528?ref_src=twsrc^tfw\">February 20, 2023</a></blockquote> <text></text> </div><h2>Art</h2><p><strong><a href=\"https://twitter.com/1upVSCPU/status/1627954055359172609\">Grubberine</a> + </strong><a href=\"https://twitter.com/1upVSCPU/status/1627646656807043074\"><strong>Comic Book-Styled Giant Bomb Logo</strong></a> (By: <a href=\"https://twitter.com/1upVSCPU\">@1upVSCPU</a>)</p><p data-right-indent=\"0\" style=\"margin-left: 30px;\">Over on Twitter, 1upVSCPU imagined Jeff Grubb gracing the cover of a comic book but OBVIOUSLY used their clean-shaven looks rather than the version of him with a glorious mane. Also, they provided a super-sized version of the Giant Bomb logo in the complete design.</p><figure data-align=\"center\" data-size=\"large\" data-img-src=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449829-1.jpg\" data-ref-id=\"1300-3449829\" data-ratio=\"1.5205913410771\" data-width=\"947\" data-embed-type=\"image\" style=\"width: 947px\"><a href=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449829-1.jpg\"><img alt=\"No Caption Provided\" src=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449829-1.jpg\" srcset=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449829-1.jpg 947w, https://www.giantbomb.com/a/uploads/scale_medium/0/8273/3449829-1.jpg 631w\" sizes=\"(max-width: 947px) 100vw, 947px\" data-width=\"947\"></a></figure><figure data-align=\"center\" data-size=\"large\" data-img-src=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449830-7994219330-FpaPUHlXEAMvnBl.jpg\" data-ref-id=\"1300-3449830\" data-ratio=\"1\" data-width=\"1080\" data-embed-type=\"image\" style=\"width: 1080px\"><a href=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449830-7994219330-FpaPUHlXEAMvnBl.jpg\"><img alt=\"No Caption Provided\" src=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449830-7994219330-FpaPUHlXEAMvnBl.jpg\" srcset=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449830-7994219330-FpaPUHlXEAMvnBl.jpg 1080w, https://www.giantbomb.com/a/uploads/scale_medium/0/8273/3449830-7994219330-FpaPUHlXEAMvnBl.jpg 960w\" sizes=\"(max-width: 1920px) 100vw, 1920px\" data-width=\"1920\"></a></figure><h2>Activities</h2><p><a href=\"https://www.giantbomb.com/forums/general-discussion-30/the-gb-album-club-025-untrue-by-burial-1909981/\"><strong>The GB Album Club 025 - Untrue By Burial</strong></a> (By: <a href=\"https://www.giantbomb.com/profile/unclejam23/\">@unclejam23</a>)</p><figure data-align=\"right\" data-size=\"small\" data-img-src=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449606-2472541590-FpnSWLYaMAAcK2r.jpg\" data-ref-id=\"1300-3449606\" data-ratio=\"0.5247619047619\" data-width=\"3150\" data-embed-type=\"image\" style=\"width: 3150px\"><a href=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449606-2472541590-FpnSWLYaMAAcK2r.jpg\"><img alt=\"Let's talk about dubstep!\" src=\"https://www.giantbomb.com/a/uploads/scale_small/0/8273/3449606-2472541590-FpnSWLYaMAAcK2r.jpg\" srcset=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449606-2472541590-FpnSWLYaMAAcK2r.jpg 3150w, https://www.giantbomb.com/a/uploads/scale_super/0/8273/3449606-2472541590-FpnSWLYaMAAcK2r.jpg 960w, https://www.giantbomb.com/a/uploads/scale_medium/0/8273/3449606-2472541590-FpnSWLYaMAAcK2r.jpg 480w, https://www.giantbomb.com/a/uploads/scale_small/0/8273/3449606-2472541590-FpnSWLYaMAAcK2r.jpg 320w\" sizes=\"(max-width: 320px) 100vw, 320px\" data-width=\"320\"></a><figcaption>Let's talk about dubstep!</figcaption></figure><p data-right-indent=\"0\" style=\"margin-left: 30px;\">Untrue by Burial is the latest pick by the Giant Bomb Community Album Club! If you have thoughts about this universally acclaimed electronic album and how it influenced the dubstep genre, share them by clicking the link above!</p><p><strong><a href=\"https://www.giantbomb.com/coffee-talk/3030-73092/forums/coffee-talk-is-the-4th-game-of-the-uugpgc-year-2-f-1909806/\">Coffee Talk Is The 4th Game Of The UUGPGC Year 2! Finish By February 27! All Are Welcome. Please Mark Spoilers!</a></strong>(By:<a href=\"https://www.giantbomb.com/profile/bigsocrates/\">@bigsocrates</a>)</p><figure data-align=\"right\" data-size=\"small\" data-img-src=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449839-5213206447-Fp0-nYyakAIoroa.jpg\" data-ref-id=\"1300-3449839\" data-ratio=\"0.5625\" data-width=\"1920\" data-embed-type=\"image\" style=\"width: 1920px\"><a href=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449839-5213206447-Fp0-nYyakAIoroa.jpg\"><img alt=\"Let's talk about cyber elfs!\" src=\"https://www.giantbomb.com/a/uploads/scale_small/0/8273/3449839-5213206447-Fp0-nYyakAIoroa.jpg\" srcset=\"https://www.giantbomb.com/a/uploads/original/0/8273/3449839-5213206447-Fp0-nYyakAIoroa.jpg 1920w, https://www.giantbomb.com/a/uploads/scale_super/0/8273/3449839-5213206447-Fp0-nYyakAIoroa.jpg 960w, https://www.giantbomb.com/a/uploads/scale_medium/0/8273/3449839-5213206447-Fp0-nYyakAIoroa.jpg 480w, https://www.giantbomb.com/a/uploads/scale_small/0/8273/3449839-5213206447-Fp0-nYyakAIoroa.jpg 320w\" sizes=\"(max-width: 320px) 100vw, 320px\" data-width=\"320\"></a><figcaption>Let's talk about cyber elfs!</figcaption></figure><p data-right-indent=\"0\" style=\"margin-left: 30px;\">Don't forget <a href=\"https://www.giantbomb.com/coffee-talk/3030-73092/\">Coffee Talk</a> is the 4th game selected for the Community Game Pass Game Club! If you have any non-spoiler impressions of the game, its structure, and general aesthetic choices, feel free to share them!</p><h2>Blogs</h2><p><strong><a href=\"https://www.giantbomb.com/profile/borgmaster/blog/all-ps1-games-in-order-part-019/269383/\">All